Future<int> slow() async {
  await Future.delayed(const Duration(milliseconds: 10));
  return 1;
}

Future<void> main() async {
  final v = await slow();
  print(v);
}

Stream<int> nums() async* {
  for (var i = 0; i < 3; i++) {
    yield i;
  }
}

Future<void> main() async {
  await for (final x in nums()) {
    print(x);
  }
}

