import "fmt"

func Filter[T any](in []T, keep func(T) bool) []T {
	out := make([]T, 0, len(in))
	for _, v := range in {
		if keep(v) {
			out = append(out, v)
		}
	}
	return out
}

func main() {
	nums := []int{1, 2, 3, 4, 5}
	even := Filter(nums, func(n int) bool { return n%2 == 0 })
	fmt.Println(even)
}

