extends Node2D

@onready var sprite: Sprite2D = $Sprite2D

func _ready():
	sprite.visible = true

const EnemyScene: PackedScene = preload("res://scenes/enemy.tscn")

func spawn():
	add_child(EnemyScene.instantiate())

@onready var button: Button = $Button

func _ready():
	button.pressed.connect(Callable(self, "_on_pressed"))

func _on_pressed():
	print("pressed")

signal died(who: Node)

func kill():
	died.emit(self)

func _physics_process(_dt):
	var x := Input.get_axis("move_left", "move_right")
	velocity.x = x * 200.0

func _unhandled_input(event):
	if event is InputEventKey and event.pressed:
		print(event.keycode)

func add(a: int, b: int) -> int:
	return a + b

func greet(name := "World"):
	print("Hello %s" % name)

extends Node2D

var score := 0
@onready var sprite: Sprite2D = $Sprite2D

func _ready():
	print(score)

func _ready():
	var c = Callable(self, "_on_done")
	c.call()

func _on_done():
	print("done")

func _physics_process(dt):
	# physics tick
	pass

if node is CharacterBody2D:
	var p := node as CharacterBody2D
	if p != null:
		print(p.velocity)

var d := {"hp": 10}
d["hp"] += 5
print(d.get("hp", 0))

for i in range(3):
	print(i)

var d := {"a": 1}
for k in d:
	print(k, d[k])

if hp <= 0:
	queue_free()
elif hp < 10:
	push_warning("low hp")

var xs := [-2, 0, 3, 5]
var pos := xs.filter(func(x): return x > 0)
print(pos)

const EnemyScene: PackedScene = preload("res://scenes/enemy.tscn")

func spawn():
	add_child(EnemyScene.instantiate())

func write_text(path: String, text: String) -> void:
	var f := FileAccess.open(path, FileAccess.WRITE)
	f.store_string(text)

func read_text(path: String) -> String:
	var f := FileAccess.open(path, FileAccess.READ)
	return f.get_as_text()

var data := {"hp": 10, "items": ["key"]}
var text := JSON.stringify(data)
var obj = JSON.parse_string(text)
if typeof(obj) == TYPE_DICTIONARY:
	print(obj["hp"])

var cfg := ConfigFile.new()
cfg.set_value("video", "fullscreen", true)
cfg.save("user://settings.cfg")

@onready var http: HTTPRequest = $HTTPRequest

func post_score(score: int) -> void:
	var body := JSON.stringify({"score": score})
	http.request("https://example.invalid/api/score", ["Content-Type: application/json"], HTTPClient.METHOD_POST, body)

var re := RegEx.new()
re.compile("\\b[a-z]+\\b")
var m := re.search("hi there 123")
if m != null:
	print(m.get_string())

var re := RegEx.new()
re.compile("\\d+")
print(re.sub("a1 b22", "#"))

var ts := Time.get_unix_time_from_system()
var dt := Time.get_datetime_dict_from_system()
print(ts)
print(dt)

var dt := Time.get_datetime_dict_from_system()
var s := "%04d-%02d-%02d_%02d-%02d-%02d" % [dt.year, dt.month, dt.day, dt.hour, dt.minute, dt.second]
print(s)

@onready var label: Label = $HUD/Label

func _ready():
	if label == null:
		push_error("HUD/Label not found")
