import re

def parse_int(s: str) -> int | None:
    try:
        return int(s.strip())
    except ValueError:
        return None

print(parse_int(' 12 '))
print(bool(re.fullmatch(r'[^@]+@[^@]+', 'a@example.com')))

def add_item(x, items=None):
    if items is None:
        items = []
    items.append(x)
    return items

xs = ['a', 'b', 'c']
for i, x in enumerate(xs):
    print(i, x)

x = 0
if x > 0:
    print('pos')
elif x == 0:
    print('zero')
else:
    print('neg')

try:
    n = int('12')
except ValueError as e:
    print('bad')
else:
    print('ok', n)
finally:
    print('done')

class User:
    def __init__(self, name: str):
        self.name = name

    def greet(self) -> str:
        return f'hi {self.name}'

print(User('Ada').greet())

from dataclasses import dataclass

@dataclass
class Point:
    x: int
    y: int

print(Point(1, 2))

from typing import Callable

def apply(n: int, f: Callable[[int], int]) -> int:
    return f(n)

print(apply(10, lambda x: x * 2))
